import React, { Component } from 'react';
import DailyHabitTracker from './DailyHabitTracker.jsx';

class WeeklyPlanner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            goals: null
        };
    }

    componentDidMount() {
        fetch('/weeks/1')
            .then(res => res.json())
            .then(weeks => this.setState({goals: weeks.goals}));
    }

    render() {
        return (
            <>
                <h1>Week [NUMBER]</h1>
                <DailyHabitTracker />
            </>
        )
    }
}

export default WeeklyPlanner;
