import React, { Component } from 'react';

class WeekInitializer extends Component {
  constructor(props) {
    super(props);
    this.state = {allHabits: null}
  }

  componentDidMount() {
    fetch('/habits')
      .then(res => res.json())
      .then(allHabits => this.setState({allHabits}));
  }

  render() {
    const displayForm = allHabits
    ? (
      <form>
        <p>this is the form where you will be able to chose new habits!</p>
      </form>
    )
    : <p>loading habits...</p>
    return (
      <h1>New week, new possibilities!</h1>
    )
  }
}

export default WeekInitializer;
