import React, { Component } from 'react';
import Checkbox from '../Checkbox';

class WeekInitializer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allHabits: null,
      checkedItems: new Map()
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    fetch('/habits')
      .then(res => res.json())
      .then(allHabits => this.setState({ allHabits }));
  }

  handleSubmit(event) {
    event.preventDefault();
    const { initializeWeek, week } = this.props;
    // console.log({...week, initialized: true})
    initializeWeek(week);
  }

  handleChange(event) {
    const item = event.target.name;
    const isChecked = e.target.checked;
    this.setState(prevState => ({checkedItems: prevState.checkedItems.set(item, isChecked)}));
  }

  render() {
    const { allHabits } = this.state;
    const displayForm = allHabits
      ? (
        <form onSubmit={this.handleSubmit}>
          {allHabits.map(habit, index => {
            return (
              <>
                <label key={`checkbox${index}`}>
                  {habit.title}
                  <Checkbox name={habit.title} checked={this.state.checkedItems.get(item.name)} onChange={this.handleChange} />
                </label>
                <br />
              </>
            )
          })}
          <input type="submit" value="Finish weekly planning" />
        </form>
      )
      : <p>loading habits...</p>
    return (
      <>
        <h1>New week, new possibilities!</h1>
        {displayForm}
      </>
    );
  }
}

export default WeekInitializer;
