import React, { Component } from 'react';
import WeeklyPlanner from './WeeklyPlanner';

class WeekApp extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.displayWeek = this.displayWeek.bind(this);
    this.fetchWeeks = this.fetchWeeks.bind(this);
  }

  componentDidMount() {
    this.fetchWeeks();
  }

  displayWeek(activeWeek) {
    this.setState({ activeWeek });
  }

  fetchWeeks() {
    fetch('/weeks')
      .then(res => res.json())
      .then(weeks => this.setState({ weeks }));
  }

  render() {
    const { weeks, activeWeek } = this.state;

    const allWeeks = weeks
    ? (<ul>
      {weeks.map(week => <li onClick={() => this.displayWeek(week)}>Week {week.weekNumber}</li>)}
    </ul>)
    : <p>loading...</p>;

    const currentWeek = activeWeek
    ? (<WeeklyPlanner week={activeWeek} fetchWeeks={this.fetchWeeks}/>)
    : null;

    return(
      <>
        {allWeeks}
        {currentWeek}
      </>
    )
  }
}

export default WeekApp;
