import React, { Component } from 'react';

class DailyHabitTracker extends Component {
    constructor(props) {
        super(props);
        this.state = { habits: null };
        this.fetchHabits = this.fetchHabits.bind(this);
    }

    componentDidMount() {
        this.fetchHabits();
    }

    fetchHabits() {
        fetch('/weeks/1')
            .then(res => res.json())
            .then(habits => {
                console.log(habits);
                this.setState({habits})
            });
    }

    toggleComplete(habit, day) {
        habit.completed[day] = !habit.completed[day];
        const options = {
            method:'PUT',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify(habit)
        }
        fetch(`/weeks/1/habits/${habit.id}`, options)
            .then(this.fetchHabits);
    }

    render() { 
        const { habits } = this.state;
        const display = habits
            ? (
                <table>
                    <tbody>
                        <tr>
                            <th>Habit</th>
                            <th>Mon</th>
                            <th>Tue</th>
                            <th>Wed</th>
                            <th>Thu</th>
                            <th>Fri</th>
                            <th>Sat</th>
                            <th>Sun</th>
                        </tr>
                        {habits.map(habit => {
                            return (<tr>
                                <td>{habit.title}</td>
                                <td className={habit.completed.mon ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'mon')}></td>
                                <td className={habit.completed.tue ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'tue')}></td>
                                <td className={habit.completed.wed ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'wed')}></td>
                                <td className={habit.completed.thu ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'thu')}></td>
                                <td className={habit.completed.fri ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'fri')}></td>
                                <td className={habit.completed.sat ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'sat')}></td>
                                <td className={habit.completed.sun ? "completed" : "not-completed"} onClick={() => this.toggleComplete(habit,'sun')}></td>       
                            </tr>)
                        })}
                    </tbody>
                </table>
            )
            : <p>loading...</p>

        return (display);
    }
}

export default DailyHabitTracker;
