import React, { Component } from 'react';


class WeeklyPlanner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            goals: null
        };
    }

    componentDidMount() {
        fetch('/weeks/1')
            .then(res => res.json())
            .then(weeks => this.setState({goals: weeks.goals}));
    }

    render() {
        const { goals } = this.state;
        const display = goals ? 
            <ul>
                {goals.map(goal => <li>{goal}</li>)}
            </ul> 
            : <p>loading...</p>
        return (
            <>
                <h1>Week [NUMBER]</h1>
                {display}
                <DailyHabitTracker />
            </>
        )
    }
}

export default WeeklyPlanner;
