import React, { Component } from 'react';

class DailyHabitTracker extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    toggleHabit(day) {
        const { week, fetchWeeks } = this.props;
        const updatedWeek = {...week};
        updatedWeek.habits.completed[day] = !updatedWeek.habits.completed[day];
        const options = {
            method:'PUT',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify(updatedWeek)
        }
        console.log(updatedWeek);
        // fetch('/weeks/${week.id}', options)
        //     .then(fetchWeeks);
    }

    render() { 
        const { week: { habits } } = this.props;
        const display = habits
            ? (
                <table>
                    <tbody>
                        <tr>
                            <th>Habit</th>
                            <th>Mon</th>
                            <th>Tue</th>
                            <th>Wed</th>
                            <th>Thu</th>
                            <th>Fri</th>
                            <th>Sat</th>
                            <th>Sun</th>
                        </tr>
                        {habits.map(habit => {
                            return (<tr>
                                <td>{habit.title}</td>
                                <td className={habit.completed.mon ? "completed" : "not-completed"} onClick={() => this.toggleHabit(0)}></td>
                                <td className={habit.completed.tue ? "completed" : "not-completed"} onClick={() => this.toggleHabit(1)}></td>
                                <td className={habit.completed.wed ? "completed" : "not-completed"} onClick={() => this.toggleHabit(2)}></td>
                                <td className={habit.completed.thu ? "completed" : "not-completed"} onClick={() => this.toggleHabit(3)}></td>
                                <td className={habit.completed.fri ? "completed" : "not-completed"} onClick={() => this.toggleHabit(4)}></td>
                                <td className={habit.completed.sat ? "completed" : "not-completed"} onClick={() => this.toggleHabit(5)}></td>
                                <td className={habit.completed.sun ? "completed" : "not-completed"} onClick={() => this.toggleHabit(6)}></td>       
                            </tr>)
                        })}
                    </tbody>
                </table>
            )
            : <p>loading...</p>

        return (display);
    }
}

export default DailyHabitTracker;
