import React, { Component } from 'react';

class WeekInitializer extends Component {
  constructor(props) {
    super(props);
    this.state = { allHabits: null }
  }

  componentDidMount() {
    fetch('/habits')
      .then(res => res.json())
      .then(allHabits => this.setState({ allHabits }));
  }

  render() {
    const { allHabits } = this.state;
    const displayForm = allHabits
      ? (
        <form>
          {allHabits.map(habit => {
            return (
              <input type="checkbox">{habit.title}</input>
            )
          })}
        </form>
      )
      : <p>loading habits...</p>
    return (
      <>
        <h1>New week, new possibilities!</h1>
        {displayForm}
      </>
    );
  }
}

export default WeekInitializer;
